/* http://www.paulirish.com/2012/box-sizing-border-box-ftw/ */

@tailwind preflight;

@tailwind components;

@tailwind utilities;

html {
	box-sizing: border-box;
}

* {
	box-sizing: inherit;
	&:before,
	&:after {
		box-sizing: inherit;
	}
}

html,
body {
	height: 100%;
	font: medium/1.4 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

.build-link a {
	display: block;
	width: 100%;
	padding: 1em;
	font-size: 2em;
}

.js-enabled .build-link {
	position: absolute;
	bottom: 0;
	width: 100%;
	margin-top: -150px;
	padding-bottom: 100px;
	text-align: center;
	transition: margin-top ease 0.75s;
	a {
		background: #222;
		color: #fff;
		border-bottom: 4px solid #000;
	}
	&:hover {
		margin-top: 0px;
	}
}

.container {
	height: 100%;
}

.layout {
	height: 100%;
	overflow: hidden;
	.item {
		float: left;
		height: 100%;
		width: 100%;
		border: 2px solid #000;
	}
}

/**
		Layouts
*/

.layout-2row .item {
	height: 50%;
}

.layout-2col .item {
	width: 50%;
}

.layout-2col-75-25 .item {
	&:first-child {
		width: 75%;
	}
	width: 25%;
}

.layout-3row .item {
	height: 33.3%;
}

.layout-3col .item {
	width: 33.3%;
}

.layout-2x2 .item {
	width: 50%;
	height: 50%;
}

.layout-2x1 {
	.item {
		width: 50%;
		height: 50%;
	}
	.item-1 {
		height: 100%;
	}
}

.layout-2x1ish {
	.item {
		width: 67%;
		height: 50%;
	}
	.item-1 {
		height: 100%;
		width: 33%;
	}
}

.layout-1x2 {
	.item {
		width: 50%;
		height: 67%;
	}
	.item-1 {
		height: 33%;
		width: 100%;
	}
}

.layout-banner-plus-3col {
	.item {
		width: 33.3%;
		height: 67%;
	}
	.item-1 {
		width: 100%;
		height: 33%;
	}
}

.layout-sidebar-plus-3row {
	.item {
		width: 50%;
		height: 33%;
		&:last-child {
			width: 50%;
			height: 34%;
		}
	}
	.item-1 {
		width: 50%;
		height: 100%;
	}
}

body {
	font: medium/1.4 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

section {
	padding: 15px 30px;
	max-width: 900px;
	margin: 1em auto;
}

h2 {
	margin: 0.5em 0;
	font-size: 19px;
	font-weight: bold;
}

ol li {
	list-style: disc;
	padding: 5px 0;
	list-style-position: inside;
}

.title input {
	min-width: 300px;
	font-size: 16px;
	margin-left: 0.25em;
}

.layout-thumb {
	height: 50px;
	width: 50px;
}

/* Visually hidden */

.js-enhanced {
	h2 {
		position: absolute;
		left: -9999em;
	}
	section {
		&.layouts {
			input,
			label span {
				position: absolute;
				left: -9999em;
			}
		}
		&.urls label {
			position: absolute;
			left: -9999em;
		}
	}
	ol li {
		list-style: none;
	}
	section {
		margin: 0 auto;
		&.layouts ol li {
			display: inline-block;
			margin: 0 5px 0 0;
			&.selected .layout {
				background: orange;
			}
		}
		&.urls ol {
			height: 400px;
			li {
				text-align: center;
				input {
					position: relative;
					top: 50%;
					-webkit-transform: translateY(-50%);
					        transform: translateY(-50%);
					width: 80%;
					font-size: 16px;
				}
			}
		}
	}
}
